package com.orobator.android.gramophone.controller.listeners;import android.app.Fragment;import android.content.Intent;import android.net.Uri;import android.widget.ListAdapter;import com.fortysevendeg.android.swipelistview.BaseSwipeListViewListener;import com.orobator.android.gramophone.controller.services.MusicPlayerService;import com.orobator.android.gramophone.model.Song;import com.orobator.android.gramophone.model.SongQueue;import com.orobator.android.gramophone.view.activities.NowPlayingActivity;import java.io.File;public class SongSwipeViewListener extends BaseSwipeListViewListener {    private ListAdapter mListAdapter;    private Fragment mFragment;    public SongSwipeViewListener(ListAdapter listAdapter, Fragment fragment) {        mListAdapter = listAdapter;        mFragment = fragment;    }    @Override    public void onOpened(int position, boolean toRight) {    }    @Override    public void onClosed(int position, boolean fromRight) {    }    @Override    public void onListChanged() {    }    @Override    public void onMove(int position, float x) {    }    @Override    public void onStartOpen(int position, int action, boolean right) {    }    @Override    public void onStartClose(int position, boolean right) {    }    @Override    public void onClickFrontView(int position) {        Song song = (Song) mListAdapter.getItem(position);        SongQueue.initializeQueue(song, false, Song.KEY_COLLECTION_TYPE_ALL, mFragment.getActivity().getApplicationContext(), null);        playSong(song);        Intent intent = new Intent(mFragment.getActivity(), NowPlayingActivity.class);        intent.putExtra(Song.KEY_SONG, song);        intent.putExtra(Song.KEY_CURSOR_POSITION, position);        intent.putExtra(Song.KEY_SONG_COLLECTION_TYPE, Song.KEY_COLLECTION_TYPE_ALL);        mFragment.startActivity(intent);    }    @Override    public void onClickBackView(int position) {    }    @Override    public void onDismiss(int[] reverseSortedPositions) {    }    private void playSong(Song song) {        Uri songUri = Uri.fromFile(new File(song.getFilePath()));        Intent intent = new Intent(MusicPlayerService.ACTION_PLAY, songUri, mFragment.getActivity().getApplicationContext(), MusicPlayerService.class);        intent.putExtra(Song.KEY_SONG, song);        mFragment.getActivity().startService(intent);    }}