package com.orobator.android.gramophone.model.loaders;import android.content.Context;import android.database.Cursor;import android.os.Bundle;import com.orobator.android.gramophone.model.Album;import com.orobator.android.gramophone.model.SongDatabaseHelper;/** * Loads an Album cursor asynchronously */public class AlbumCursorLoader extends SQLiteCursorLoader {    public static final int ALL_ALBUMS_ID = 0;    public static final int ARTIST_ALBUMS_ID = 1;    private int mID;    private Bundle mArgs;    private SongDatabaseHelper mHelper;    public AlbumCursorLoader(Context context, int ID, Bundle args) {        super(context);        mID = ID;        mArgs = args;        mHelper = new SongDatabaseHelper(context);    }    @Override    protected Cursor loadCursor() {        switch (mID) {            case ALL_ALBUMS_ID:                return mHelper.queryAlbums();            case ARTIST_ALBUMS_ID:                String artist = mArgs.getString(Album.KEY_ALBUM_ARTIST);                return mHelper.queryAlbumsForArtist(artist);            default:                return mHelper.queryAlbums();        }    }}