package com.orobator.android.gramophone.view.fragments;import android.app.Fragment;import android.os.Bundle;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ListView;import com.orobator.android.gramophone.R;import com.orobator.android.gramophone.view.adapters.SongQueueAdapter;public class SongQueueFragment extends Fragment {    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup parent, Bundle savedInstanceState) {        View view = inflater.inflate(R.layout.list_view_song_queue, parent, false);        ListView queueListView = (ListView) view.findViewById(R.id.songQueue_listView);        queueListView.setAdapter(new SongQueueAdapter(getActivity().getApplicationContext()));        return view;    }}