package com.orobator.android.gramophone.view.fragments;import android.app.Fragment;import android.os.Bundle;import android.util.Log;import android.view.LayoutInflater;import android.view.View;import android.view.ViewGroup;import android.widget.ListView;import com.orobator.android.gramophone.R;import com.orobator.android.gramophone.model.SongQueue;import com.orobator.android.gramophone.view.adapters.SongQueueAdapter;public class SongQueueFragment extends Fragment {    private static final String TAG = "SongQueueFragment";    private ListView mQueueListView;    @Override    public View onCreateView(LayoutInflater inflater, ViewGroup parent, Bundle savedInstanceState) {        View view = inflater.inflate(R.layout.list_view_song_queue, parent, false);        mQueueListView = (ListView) view.findViewById(R.id.songQueue_listView);        mQueueListView.setAdapter(new SongQueueAdapter(getActivity().getApplicationContext()));        View emptyView = view.findViewById(R.id.emptyView);        mQueueListView.setEmptyView(emptyView);        Log.d(TAG, SongQueue.displayQueue());        return view;    }    @Override    public void onResume() {        super.onResume();        mQueueListView.deferNotifyDataSetChanged();    }}